function params = load_dataset_bsnip()
    ica_path = '/data/users2/salman/projects/COBRE/current/results/ica_results_old';
    % this is manually generated by resolving the ICA selected subject list and the OSUCH diagnosis spreadsheet
    sub_info_file = '/data/users2/salman/projects/COBRE/current/data/cobre1_meta.mat';
    ic_labels = '/data/users2/salman/projects/COBRE/current/results/cobre1_rsn_old.mat';
    
    % get number of voxels from param file
    t1 = strip( ls( [ica_path '/*param*'] ) );
    t1 = load( t1 );
    n_voxels = numel( t1.sesInfo.mask_ind );

    % grab the domain labels
    ic_labels = load( ic_labels );
    ic_labels.ic_meta( :, 2 ) = [];
    ic_labels = ic_labels.ic_meta;
    domains = unique( sort( ic_labels(:,2) ) );

    % grab the subject labels
    y = load( sub_info_file );
    y = y.subjects(:, end);
    y = cell2mat(y);
    sub_idx = 1:length( y );

    % which file to load from ICA output
    file_pat = fullfile( ica_path, 'cobre1_ica_c$1-1.mat' );

    % return values
    params.file_pat = file_pat;
    params.y = y;
    params.n_voxels = n_voxels;
    params.ic_labels = ic_labels;
    params.domains = domains;
    params.sub_idx = sub_idx;


