% zfu_prep- means preprocessed by Zening but only the subjects used in the original paper
function params = load_dataset_zfu_prep_nm_ref()
    ica_path = '../results/nmref_gig_ica_zfu_prepdata_only/'
    % this is manually generated by resolving the ICA selected subject list and the OSUCH diagnosis spreadsheet
    sub_info_file = '../data/zfu_prep_osuch_ref/labels.csv'
    neuromark_ic_labels = '../results/neuromark_ic_labels.csv';
    
    % get number of voxels from param file
    t1 = strip( ls( [ica_path '/*param*'] ) );
    t1 = load( t1 );
    n_voxels = numel( t1.sesInfo.mask_ind );

    % grab the domain labels
    ic_labels = readcell( neuromark_ic_labels );
    domains = unique( sort( ic_labels(:,2) ) )

    % grab the subject labels
    y = readcell( sub_info_file );
    y = double( categorical( y ) );
    disp( ['classes: '] )
    unique(y)
    sub_idx = 1:length( y )

    % which file to load from ICA output
    file_pat = fullfile( ica_path, 'osuchref_ica_c$1-1.mat' );

    % return values
    params.file_pat = file_pat;
    params.y = y;
    params.n_voxels = n_voxels;
    params.ic_labels = ic_labels;
    params.domains = domains;
    params.sub_idx = sub_idx;


